import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import firebase from '../infocomponents/firebase';

function FillHistoryComponent() {
	const [ noHistory, setHistory ] = useState(true);
	const [ userId, setUID ] = useState(' ');

	firebase.auth().onAuthStateChanged(function(resultUser) {
		if (resultUser) {
			console.log('USER LOGGED IN');
			setUID(resultUser.uid);
			firebase
				.firestore()
				.collection('approvedOrders')
				.doc(userId)
				.collection('invoice')
				.get()
				.then((querySnapshot) => {
					console.log(querySnapshot);
					const data = querySnapshot.docs.map((doc) => doc.data());
					const listHistory = data.map((e) => {
						historyList(e);
					});
				});
		} else {
			// No user is signed in.
			console.log('USER NOT LOGGED IN');
		}
	});

	function historyList(e) {
		console.log(e);
	}

	return (
		<div>
			<Card bg="transparent">
				<Card.Header className="slogan-1">
					<h4>History</h4>
				</Card.Header>
				<Container>
					<Row>
						<Col />
					</Row>
				</Container>
			</Card>
		</div>
	);
}

export default FillHistoryComponent;
