import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import BookSlotComponent from '../BookSlotComponent';
import { db } from '../infocomponents/firebase';

class BodyBookingComponent extends Component {
	constructor() {
		super();
		this.state = {
			firstName: 'First Name',
			lastName: 'Last Name',
			email: 'example@email.com',
			pin: '000000',
			brand: 'trek',
			data: {},
			serviceType: 'none',
			model: 'trek00000',
			phone: '0000000000',
			slot: '9:00-12:00'
		};
		this.handleOnSubmit = this.handleOnSubmit.bind(this);
	}

	handleOnSubmit(event) {
		let info = {}; //create an empty object
		let date = new Date();
		info.firstName = document.getElementById('formFirstName').value;
		info.lastName = document.getElementById('formLastName').value;
		info.emailId = document.getElementById('formEmail').value;
		info.slotTime = document.getElementById('formSelectSlot').value;
		info.streetName = document.getElementById('formStreet').value;
		info.pinNo = document.getElementById('formPIN').value;
		info.cityName = document.getElementById('formCity').value;
		info.DistrictName = document.getElementById('formDistrict').value;
		info.State = document.getElementById('formState').value;
		info.emailId = document.getElementById('formEmail').value;
		info.phoneNo = document.getElementById('formPhone').value;
		info.serviceType = document.getElementById('formSelectServiceType').value;
		info.brandName = document.getElementById('formSelectBrand').value;
		info.modelNo = document.getElementById('formModel').value;
		let year = date.getFullYear();
		let month = date.getMonth() + 1;
		let day = date.getDate();
		let dateNameId =
			year.toString() +
			'/' +
			month.toString() +
			'/' +
			day.toString() +
			'|' +
			info.firstName +
			'|' +
			info.lastName;

		if (
			info.firstName === '' &&
			info.lastName === '' &&
			info.emailId === '' &&
			info.emailId === '' &&
			info.streetName === '' &&
			info.cityName === '' &&
			info.pinNo === '' &&
			info.DistrictName === '' &&
			info.State === '' &&
			info.phoneNo === '' &&
			info.modelNo === ''
		) {
			alert('Enter All Details To Submit');
		} else {
			db
				.collection('orders')
				.doc(dateNameId)
				.set({
					firstName: info.firstName,
					lastName: info.lastName,
					email: info.emailId,
					city: info.cityName,
					pin: info.pinNo,
					street: info.streetName,
					state: info.State,
					district: info.DistrictName,
					brand: info.brandName,
					serviceType: info.serviceType,
					model: info.modelNo,
					phone: info.phoneNo,
					slot: info.slotTime
				})
				.then(function() {
					console.log('Document successfully written!');
					alert('Your Request Is Submitted');
				})
				.catch(function(error) {
					console.error('Error writing document: ', error);
				});
		}
	}

	render() {
		return (
			<div>
				<Container>
					<Row>
						<Col>
							<Card bg="transparent">
								<Card.Header>
									<h3 className="slogan-1 font-weight-bold">Fill the details</h3>
								</Card.Header>
								<Container>
									<Row className="justify-content-center">
										<Col md={8} className="my-auto">
											<br />
											<Form bg="transparent">
												<Form.Group controlId="formFirstName" required>
													<Form.Label>Enter Your First Name</Form.Label>
													<Form.Control type="name" placeholder="First Name" />
												</Form.Group>
												<Form.Group controlId="formLastName">
													<Form.Label>Enter Your Last Name</Form.Label>
													<Form.Control type="name" placeholder="Last Name" />
												</Form.Group>
												<Form.Group controlId="formEmail">
													<Form.Label>Email address</Form.Label>
													<Form.Control type="email" placeholder="Email" />
													<Form.Text className="text-muted">
														We'll never share your email with anyone else.
													</Form.Text>
												</Form.Group>
												<Form.Group controlId="formSelectSlot">
													<Form.Label>Select Your Slot</Form.Label>
													<Form.Control as="select" placeholder="Available Slots">
														<option>9:00-12:00</option>
														<option>12:00-15:00</option>
														<option>15:00-18:00</option>
														<option>18:00-21:00</option>
													</Form.Control>
												</Form.Group>
												<Form.Group controlId="formStreet">
													<Form.Label>Enter Street Name</Form.Label>
													<Form.Control type="text" placeholder="Street" />
												</Form.Group>
												<Form.Group controlId="formCity">
													<Form.Label>Enter City</Form.Label>
													<Form.Control type="text" placeholder="City" />
												</Form.Group>
												<Form.Group controlId="formPIN">
													<Form.Label>Enter PIN</Form.Label>
													<Form.Control type="number" placeholder="PIN Code" />
												</Form.Group>
												<Form.Group controlId="formDistrict">
													<Form.Label>Enter Your District</Form.Label>
													<Form.Control type="text" placeholder="District" />
												</Form.Group>
												<Form.Group controlId="formState">
													<Form.Label>Enter Your State</Form.Label>
													<Form.Control type="text" placeholder="State" />
												</Form.Group>
												<Form.Group controlId="formPhone">
													<Form.Label>Enter Phone No.</Form.Label>
													<Form.Control type="number" placeholder="Phone No." />
													<Form.Text className="text-muted">
														We'll never share your number with anyone else.
													</Form.Text>
												</Form.Group>
												<Form.Group controlId="formSelectServiceType">
													<Form.Label>Select Service Type</Form.Label>
													<Form.Control as="select" placeholder="Service Type">
														<option>SERVICE AT HOME</option>
														<option>PICK UP AND DROP SERVICE</option>
													</Form.Control>
												</Form.Group>
												<Form.Group controlId="formSelectBrand">
													<Form.Label>Brand</Form.Label>
													<Form.Control as="select" placeholder="Brand Name">
														<option>Trek</option>
														<option>Ducati</option>
														<option>Kross</option>
														<option>Lapierre</option>
														<option>Bianchi</option>
														<option>Cannondale</option>
														<option>Raleigh</option>
														<option>Btwin</option>
														<option>Other</option>
													</Form.Control>
												</Form.Group>
												<Form.Group controlId="formModel">
													<Form.Label>Enter Your Model</Form.Label>
													<Form.Control type="text" placeholder="Model No" />
												</Form.Group>
												<br />
												<Button
													variant="primary"
													onClick={this.handleOnSubmit}
													id="submitButton"
												>
													Submit
												</Button>
											</Form>
											<br />
										</Col>
									</Row>
								</Container>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default BodyBookingComponent;
