import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import firebase from '../infocomponents/firebase';

function FillHistoryComponent() {
	const [ noHistory, setHistory ] = useState(true);
	const [ userId, setUID ] = useState(' ');
	const historyData = document.querySelector('#historyList');

	firebase.auth().onAuthStateChanged(function(resultUser) {
		setUID(resultUser.uid);
	});

	function renderHistory(doc) {
		let li = document.createElement('li');
		let transactionId = document.createElement('span');
		let shopName = document.createElement('span');
		let date = document.createElement('span');
		let totalAmount = document.createElement('span');
		let modeOfPayment = document.createElement('span');

		li.appendChild(transactionId);
		li.appendChild(shopName);
		li.appendChild(date);
		li.appendChild(totalAmount);
		li.appendChild(modeOfPayment);

		historyData.appendChild(li);
	}

	firebase.firestore().collection('approvedOrders').doc(userId).collection('invoice').get().then((querySnapshot) => {
		querySnapshot.docs.forEach((doc) => {
			renderHistory(doc);
		});
	});

	return (
		<div>
			<Card bg="transparent">
				<Card.Header className="slogan-1">
					<h4>History</h4>
				</Card.Header>
				<Container>
					<Row>
						<Col>
							<div id="historyList" />
						</Col>
					</Row>
				</Container>
			</Card>
		</div>
	);
}

export default FillHistoryComponent;
