import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link } from 'react-router-dom';
import firebase from '../infocomponents/firebase';

function DashboardBodyComponent() {
	const [ userName, setUser ] = useState('LOGIN');
	const [ name, setName ] = useState(' ');
	const [ openEdit, setEdit ] = useState(false);
	const [ loginStatus, setStatus ] = useState('');

	function signOutUser() {
		firebase
			.auth()
			.signOut()
			.then(function() {
				console.log('Sign-out successful');
			})
			.catch(function(error) {
				console.log('An error happened');
			});
	}
	firebase.auth().onAuthStateChanged(function(resultUser) {
		if (resultUser) {
			console.log('USER LOGGED IN');
			setStatus('SignOut');
			let user = resultUser.email;
			if (user != null) setUser(`${user.substr(0, user.indexOf('@'))}`);
			else setUser(`Hi, ${resultUser.phoneNumber}`);
		} else {
			// No user is signed in.
			console.log('USER NOT LOGGED IN');
			setUser('LOGIN');
			setStatus('');
		}
	});
	return (
		<div>
			<Container>
				<Row>
					<Col>
						<Card className="text-center  mb-5 " border="dark" bg="transparent">
							<Card.Header className="slogan-1 display-4">Dashboard User</Card.Header>
							<Card.Body>
								<Container>
									<Row>
										<Col md={4}>
											<Card bg="transparent">
												<Card.Header className="slogan-2">
													<h4>Personal</h4>
												</Card.Header>
												<Card.Text />
												<Card.Footer className="text-muted">
													<button className="btn btn-outline-dark ">
														<Link className="text-dark" to="/booking">
															Edit Details
														</Link>
													</button>
												</Card.Footer>
											</Card>
										</Col>
										<Col md={4}>
											<Card bg="transparent">
												<Card.Header className="slogan-2">
													<h4>Address</h4>
												</Card.Header>
												<Card.Text />
											</Card>
										</Col>
										<Col md={4}>
											<Card bg="transparent">
												<Card.Header className="slogan-2">
													<h4>History</h4>
												</Card.Header>
												<Card.Text />
											</Card>
										</Col>
									</Row>
								</Container>
							</Card.Body>
							<Card.Footer className="text-muted">
								<button className="btn btn-outline-dark ">
									<Link className="text-light" to="/booking">
										Book a slot
									</Link>
								</button>
							</Card.Footer>
						</Card>
					</Col>
				</Row>
			</Container>
			<Container>
				<Row>
					<Col />
				</Row>
			</Container>
		</div>
	);
}

export default DashboardBodyComponent;
