import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import firebase from '../infocomponents/firebase';

function FillHistoryComponent() {
	const [ noHistory, setHistory ] = useState(true);
	const [ userId, setUID ] = useState(' ');
	const historyData = document.querySelector('#historyList');

	firebase.auth().onAuthStateChanged(function(resultUser) {
		setUID(resultUser.uid);
	});

	useEffect(
		() => {
			firebase
				.firestore()
				.collection('approvedOrders')
				.doc(userId)
				.collection('invoice')
				.get()
				.then((querySnapshot) => {
					querySnapshot.docs.forEach((doc) => {
						renderHistory(doc);
					});
				});
		},
		[ userId ]
	);

	function renderHistory(doc) {
		let li = document.createElement('div');
		let gap_1 = document.createElement('br');
		let gap_2 = document.createElement('br');
		let container = document.createElement('div');
		let transactionId = document.createElement('div');
		let shopName = document.createElement('div');
		let date = document.createElement('div');
		let totalAmount = document.createElement('div');
		let modeOfPayment = document.createElement('div');

		li.setAttribute('data-id', doc.id);
		li.className = 'card-history';
		container.className = 'container-history';
		transactionId.textContent = doc.data().transactionId;
		shopName.textContent = doc.data().shopName;
		date.textContent = doc.data().transactionDate;
		totalAmount.textContent = doc.data().totalAmount;
		modeOfPayment.textContent = doc.data().modeOfPayment;
		li.appendChild(transactionId);
		li.appendChild(shopName);
		li.appendChild(date);
		li.appendChild(totalAmount);
		li.appendChild(modeOfPayment);

		historyData.appendChild(li);
		historyData.appendChild(gap_1);
		historyData.appendChild(gap_2);
	}

	return (
		<div>
			<Card bg="transparent">
				<Card.Header className="slogan-1">
					<h4>History</h4>
				</Card.Header>
				<Container>
					<Row>
						<Col>
							<div id="historyList" className="mt-5 mb-1" />
						</Col>
					</Row>
				</Container>
			</Card>
		</div>
	);
}

export default FillHistoryComponent;
