import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import LogNavComponent from '../LogNavComponent';
import firebase from '../infocomponents/firebase';
import { Link } from 'react-router-dom';

function OtpComponent() {
	setTimeout(function() {
		window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('recaptcha-container', {
			callback: function(response) {
				submitPhoneNumberAuth();
			}
		});
	}, 2000);

	// This function runs when the 'sign-in-button' is clicked
	// Takes the value from the 'phoneNumber' input and sends SMS to that phone number
	function submitPhoneNumberAuth() {
		var phoneNumber = document.getElementById('formPhone').value;
		var appVerifier = window.recaptchaVerifier;
		firebase
			.auth()
			.signInWithPhoneNumber(phoneNumber, appVerifier)
			.then(function(confirmationResult) {
				window.confirmationResult = confirmationResult;
			})
			.catch(function(error) {
				console.log(error);
			});
	}

	// This function runs when the 'confirm-code' button is clicked
	// Takes the value from the 'code' input and submits the code to verify the phone number
	// Return a user object if the authentication was successful, and auth is complete
	function submitPhoneNumberAuthCode() {
		var code = document.getElementById('formCode').value;
		firebase.auth.confirmationResult
			.confirm(code)
			.then(function(result) {
				var user = result.user;
				console.log(user);
			})
			.catch(function(error) {
				console.log(error);
			});
	}
	return (
		<div>
			<LogNavComponent />
			<Container className="Login-component" fluid>
				<Container className="pt-5 ">
					<Container className="pt-5 ">
						<Container className="pt-5 ">
							<Row className="justify-content-center">
								<Col md={7} lg={6} className="my-auto">
									<Card>
										<Card.Header>
											<h3 className="slogan-1 font-weight-bold">Otp Login</h3>
										</Card.Header>
										<Card.Body>
											<Form>
												<Form.Group controlId="formPhone">
													<Form.Label>Phone Number</Form.Label>
													<Form.Control type="number" placeholder="Phone" />
												</Form.Group>
												<Form.Group controlId="formCode">
													<Form.Label>Verification Code</Form.Label>
													<Form.Control type="password" placeholder="Code" />
													<Link
														to="/login"
														style={{ textDecoration: 'none', color: 'white' }}
													>
														<Form.Text className="text-muted pt-2">
															Sign In with Email
														</Form.Text>
													</Link>
												</Form.Group>
												<div id="recaptcha-container" />
												<Button
													variant="primary"
													id="submitButton"
													className="mt-3"
													onClick={submitPhoneNumberAuthCode}
												>
													Login
												</Button>
											</Form>
										</Card.Body>
									</Card>
									<Container className="mt-5 ">
										<Row className="justify-content-center">
											<Col md={10} className="my-auto pl-5">
												<Link to="/register" style={{ textDecoration: 'none', color: 'white' }}>
													<h4 className="slogan-1 text-light text-center">
														{' '}
														New to FITOM ? Register{' '}
													</h4>
												</Link>
											</Col>
										</Row>
									</Container>
								</Col>
							</Row>
						</Container>
					</Container>
				</Container>
			</Container>
		</div>
	);
}

export default OtpComponent;
