					// 	.set({
					// 		firstName: info.firstName,
					// 		lastName: info.lastName,
					// 		city: info.cityName,
					// 		pin: info.pinNo,
					// 		street: info.streetName,
					// 		state: info.State,
					// 		district: info.DistrictName,
					// 		brand: info.brandName,
					// 		serviceType: info.serviceType,
					// 		model: info.modelNo,
					// 		phone: info.phoneNo,
					// 		slot: info.slotTime
					// 	})
					// 	.then(function () {
					// 		console.log('Document successfully written!');
					// 		alert('Your Request Is Submitted');
					// 	})
					// 	.catch(function (error) {
					// 		console.error('Error writing document: ', error);
					// 	});
				})
				.catch(function(error) {
					console.log('An error happened');
				});
		}
	}

	return (
		<div>
			<LogNavComponent />
			<Container className="Login-component" fluid>
				<Container className="pt-3 ">
					<Container className="pt-1 ">
						<Container className="pt-1 ">
							<Row className="justify-content-center">
								<Col md={8} className="my-auto">
									<Card>
										<Card.Header>
											<h3 className="slogan-1 font-weight-bold">Register</h3>
										</Card.Header>
										<Card.Body>
											<Form>
												<Form.Group controlId="formUserName">
													<Form.Label>Enter your username</Form.Label>
													<Form.Control type="name" placeholder="Username" />
												</Form.Group>
												<Form.Group controlId="formPhone">
													<Form.Label>Enter your phone no.</Form.Label>
													<Form.Control type="number" placeholder="Phone" />
												</Form.Group>
												<Form.Group controlId="formEmail">
													<Form.Label>Enter your email address</Form.Label>
													<Form.Control type="email" placeholder="Email" />
												</Form.Group>
												<Form.Group controlId="formPassword">
													<Form.Label>Password</Form.Label>
													<Form.Control type="password" placeholder="Password" />
												</Form.Group>
												<Button
													variant="primary"
													id="submitButton"
													className="mt-3"
													onClick={registerUser}
												>
													Register
												</Button>
											</Form>
										</Card.Body>
									</Card>
									<Container className="mt-5 ">
										<Row className="justify-content-center">
											<Col md={9} className="my-auto pl-5">
												<Link to="/login" style={{ textDecoration: 'none', color: 'white' }}>
													<h4 className="slogan-1 text-light text-center">
														{' '}
														Already Signed Up ? Login{' '}
													</h4>
												</Link>
											</Col>
										</Row>
									</Container>
								</Col>
							</Row>
						</Container>
					</Container>
				</Container>
			</Container>
		</div>
	);
}

export default RegisterComponent;

