import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import firebase from '../infocomponents/firebase';

function FillHistoryComponent() {
	const [ noHistory, setHistory ] = useState(true);
	const [ hList, setHList ] = useState([]);
	const [ userId, setUID ] = useState(' ');

	useEffect(
		() => {
			firebase.auth().onAuthStateChanged(function(resultUser) {
				if (resultUser) {
					console.log('USER LOGGED IN');
					setUID(resultUser.uid);
					firebase
						.firestore()
						.collection('approvedOrders')
						.doc(userId)
						.collection('invoice')
						.get()
						.then((querySnapshot) => {
							const data = querySnapshot.docs.map((doc) => doc.data());
							setHList(data);
							console.log(data);
						});
				} else {
					// No user is signed in.
					console.log('USER NOT LOGGED IN');
				}
			});
		},
		[ userId ]
	);

	function showList() {
		hList.map((e) => {
			console.log(e);
		});
		return <div />;
	}

	return (
		<div>
			<Card bg="transparent">
				<Card.Header className="slogan-1">
					<h4>History</h4>
				</Card.Header>
				<Container>
					<Row>
						<Col>{showList}</Col>
					</Row>
				</Container>
			</Card>
		</div>
	);
}

export default FillHistoryComponent;
