import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import firebase from '../infocomponents/firebase';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

function FillPersonalComponent() {
	const [ userName, setUser ] = useState('LOGIN');
	const [ name, setName ] = useState(' ');
	const [ openEdit, setEdit ] = useState(false);
	const [ loginStatus, setStatus ] = useState('');

	function signOutUser() {
		firebase
			.auth()
			.signOut()
			.then(function() {
				console.log('Sign-out successful');
			})
			.catch(function(error) {
				console.log('An error happened');
			});
	}
	firebase.auth().onAuthStateChanged(function(resultUser) {
		if (resultUser) {
			console.log('USER LOGGED IN');
			setStatus('SignOut');
			let user = resultUser.email;
			if (user != null) setUser(`${user.substr(0, user.indexOf('@'))}`);
			else setUser(`Hi, ${resultUser.phoneNumber}`);
		} else {
			// No user is signed in.
			console.log('USER NOT LOGGED IN');
			setUser('LOGIN');
			setStatus('');
		}
	});

	function onHandleEditPersonal() {}

	return (
		<div>
			<Container>
				<Row>
					<Col>
						<Card className="text-center  mb-5 " border="dark" bg="transparent">
							<Card.Header className="slogan-1 display-4">Dashboard User</Card.Header>
							<Card.Body>
								<Container>
									<Row>
										<Col md={6}>
											<Card bg="transparent">
												<Card.Header className="slogan-2">
													<h4>Personal</h4>
												</Card.Header>
												<Card.Text />
												<Card.Footer className="text-muted">
													<button
														className="btn btn-outline-dark "
														onClick={onHandleEditPersonal}
													>
														<Link className="text-warning" to="/booking">
															Edit Details
														</Link>
													</button>
												</Card.Footer>
											</Card>
										</Col>
										<Col md={6}>
											<Card bg="transparent">
												<Card.Header className="slogan-2">
													<h4>Address</h4>
												</Card.Header>
												<Card.Text />
												<Card.Footer className="text-muted">
													<button
														className="btn btn-outline-dark "
														onClick={onHandleEditPersonal}
													>
														<Link className="text-warning" to="/booking">
															Edit Details
														</Link>
													</button>
												</Card.Footer>
											</Card>
										</Col>
									</Row>
								</Container>
							</Card.Body>
							<Card.Footer className="text-muted">
								<Card.Text>
									<Link className="text-dark slogan-1" to="/">
										FitOM Technologies
									</Link>
								</Card.Text>
							</Card.Footer>
						</Card>
					</Col>
				</Row>
			</Container>
		</div>
	);
}

export default FillPersonalComponent;
